import React from 'react'
import '../App.css'

class Player extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      'songname': 'AC_DC - Code Red',
      'songpath': '/Music/rock/AC_DC - Code Red (Official Audio).mp3'
    }
  }




  render() {

    return (
      <div className="container-fluid bg-secondary p-2" style={{ height: '10%', padding: '0 !important', textAlign: 'center' }} >
        <marquee><h4 className="disply-4">{this.state.songname}</h4></marquee>
        <audio style={{ width: '100%' }} controls="controls">

          <source src={this.state.songpath} />
        </audio>
      </div>
    )
  }
}

export default Player;
